import React, { createRef } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Map, TileLayer, Marker, Popup } from 'react-leaflet'
import {
  Container,
  Row,
  Col
} from 'react-bootstrap';
import { LocationEvent } from 'leaflet';

type State = {
    hasLocation: boolean,
    latlng: {
        lat: number,
        lng: number,
    },
}

class EventsExample extends React.Component<{}, State> {
    state = {
        hasLocation: false,
        latlng: {
        lat: 51.505,
        lng: -0.09,
        },
    }

    mapRef = createRef<Map>()

    handleClick = () => {
        const map = this.mapRef.current
        if (map != null) {
        map.leafletElement.locate()
        }
    }

    handleLocationFound = (e: LocationEvent) => {
        this.setState({
            hasLocation: true,
            latlng: e.latlng,
        })
    }

    render() {
        const marker = this.state.hasLocation ? (
        <Marker position={this.state.latlng}>
            <Popup>You are here</Popup>
        </Marker>
        ) : null

        return (
            <Map center={this.state.latlng} length={4} onClick={this.handleClick} onLocationfound={this.handleLocationFound} ref={this.mapRef} zoom={13}>
                <TileLayer
                attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />
                {marker}
            </Map>
        )
    }
}

function MyLocation() {
  return (
    <div id="my-location-page">
      <Container>
        <Row>
          <Col>
            <header>
              <h1 className="display-4">My Location</h1>
              <p className="lead">Marker shows where you are</p>
            </header>
          </Col>
        </Row>
      </Container>
      <EventsExample />
    </div>
  );
}

export default MyLocation;
