import React from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Map, Marker, Popup, TileLayer, LayersControl, FeatureGroup } from 'react-leaflet'
import {
  Container,
  Row,
  Col,
  Navbar,
  Nav
} from 'react-bootstrap';
import {
  BrowserRouter as Router,
  Switch,
  Route
} from "react-router-dom";
import HeatmapLayer from "react-leaflet-heatmap-layer/lib/HeatmapLayer";
import data from "./rodents.json";

class LeafletMap extends React.Component<{}> {
  render() {
    const position = { lat: 42.35, lng: -71.08 };
    var addressPoints = data.features.map(function(rat) {
      // the heatmap plugin wants an array of each location
      var location = rat.geometry.coordinates.reverse();
      //location.push(0.5);
      return location; // e.g. [50.5, 30.5, 0.2], // lat, lng, intensity
    });
    console.log(addressPoints);
    return (
      <Map center={position} zoom={13} style={{height: '600px'}}>
        <LayersControl>
          <LayersControl.BaseLayer name="Base" checked>
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution="&copy; <a href=http://osm.org/copyright>OpenStreetMap</a> contributors"
            />
          </LayersControl.BaseLayer>
          <LayersControl.Overlay name="Heatmap" checked>
            <FeatureGroup color="purple">
              <Marker position={[50.05, -0.09]} >
                <Popup>
                  <span>A pretty CSS3 popup.<br /> Easily customizable. </span>
                </Popup>
              </Marker>
              <HeatmapLayer
                fitBoundsOnLoad
                fitBoundsOnUpdate
                points={addressPoints}
                longitudeExtractor={m => m[1]}
                latitudeExtractor={m => m[0]}
                intensityExtractor={m => parseFloat(m[2])}
              />
            </FeatureGroup>
          </LayersControl.Overlay>
          <LayersControl.Overlay name="Marker">
            <FeatureGroup color="purple">
              <Marker position={position} >
                <Popup>
                  <span>A pretty CSS3 popup.<br /> Easily customizable. </span>
                </Popup>
              </Marker>
            </FeatureGroup>
          </LayersControl.Overlay>
        </LayersControl>
      </Map>
    );
  }
}
function App() {
  return (
    <Router>
      <div className="App">
        <Navbar bg="dark" variant="dark">
          <Navbar.Brand href="/">Tcheq.me</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mr-auto">
              <Nav.Link href="/">Home</Nav.Link>
              <Nav.Link href="/about">About</Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Navbar>
        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          <Route exact path="/about">
            <About />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}


function Home() {
  return (
    <div id="home-page">
      <Container>
        <Row>
          <Col>
            <header>
              <h1 className="display-4">Testing page</h1>
              <p className="lead">First page for project</p>
            </header>
          </Col>
        </Row>
        <Row>
          <Col>
            <LeafletMap />
          </Col>
        </Row>
      </Container>
    </div>
  );
}

function About() {
  return (
    <div id="about-page">
      <Container>
        <Row>
          <Col>
            <header>
              <h1 className="display-4">About page</h1>
              <p className="lead">First page for project</p>
            </header>
            <div>

            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
